"""In this tutorial I am teaching my friend about OOPThe concept is for very beginner learners."""class Computer(object):    BDT = 2000    def __init__(self, brand, Hdrive, CPU):        self.brand = brand        self.Hdrive = Hdrive        self.CPU = CPU        self.show_name = "Dhaka show"    def config(self):        return f"Brand Name: {self.brand}\nDrive:{self.Hdrive}\nCPU: {self.CPU}"    def udpate(self):        self.show_name = "Uttara shop"        return self.show_name    def setter(self, value):        self.brand = value        return self.brand    class MacBook(object):        def __init__(self):            self.ram = 16            self.price = 95000        def get_all(self):            p = f"Ram: {self.ram} And Price: {self.price}"            print(p)if __name__ == '__main__':    computer = Computer("Apple", "128SSD", "i5")    laptop = Computer("MacBook Ari", "128SSD", "i5")    print(computer.config())    Computer.config(computer)    print(laptop.udpate())    print(laptop.setter("Lenovo laptop"))    laptop.MacBook().get_all()    print(id(computer))    print(id(laptop))    print('\n\n')class Mac(Computer):    def __init__(self, brand, Hdrive, CPU, username):        super(Mac, self).__init__(brand, Hdrive, CPU)        self.username = username    def get_all(self):        title = f"My laptop brand: {self.brand}\nTotal SSD: {self.Hdrive}\nCPU status: {self.CPU}\nUsername: {self.username}"        return title    def config(self):        print("I am called form Mac class config...")        print(super().config())    def current_money(self):        self.BDT = 20        print(self.BDT)if __name__ == '__main__':    super_computer = Mac("Apple", "256SSD", 'i7', 'Sagor')    super_computer.MacBook().get_all()    super_computer.config()    super_computer.current_money()    obj = super_computer.get_all()    print(obj)