class Node(object):    def __init__(self, value):        self.next = None        self.pre = None        self.val = valueclass Double(object):    def __init__(self):        self.head = None        self.tail = None        self.size = 0    def add(self, val):        node = None(val)        if self.tail is None:            self.head = node            self.tail = node            self.size += 1        else:            self.head.next = node            self.head.pre = node            self.tail = node            self.size = +1double_one = Double()double_one.add(4)double_one.add(2)double_one.add(7)print(double_one)