import mathclass PrimeNumber(object):    # class variable    user_input = int(input("Enter number: "))    def is_prime(self, num):        for i in range(2, int(math.sqrt(num) + 1)):            if num % i == 0:                return False            else:                return Trueif __name__ == '__main__':    solver = PrimeNumber()    for i in range(1, solver.user_input + 1):        if solver.is_prime(i):            print(i)